const setCfg = flow.get('setCfg');
const getCfg = flow.get('getCfg');
const device = getCfg('device');

switch(msg.payload.cmd) {
    case 'write_ack':
    case 'report':
        return [msg, null];
    case 'disconnected':
        setCfg('gwStatus', 'disconnected');
        device.state = 'disconnected';
        return [null, { payload: { state: 'disconnected' } }];
    default:
        break;
}

function setCfg(key, value) {
    flow.set(key, value, 'default'); // ram
    flow.set(key, value, 'file'); // filestorage
}

function getCfg(key) {
    return flow.get(key);
}

function getModelId(model) {
    let sanitized = model.replace(/[^a-z0-9-]/g, '-');

    sanitized = sanitized.replace(/^-+|-+$/g, '');

    return sanitized;
}

function beautifyModelId(id) {
    let beautified = id.replace(/-/g, ' ');

    beautified = beautified.replace(/\b\w/g, function(char) {
        return char.toUpperCase();
    });

    return beautified;
}

function getSensorId(model, sid) {
    let cfg = getCfg('MODEL_SID_TREE');
    const validModelId = getModelId(model);

    if (!cfg) cfg = {};
    if (!cfg[model]) cfg[model] = [];

    let idx = cfg[model].indexOf(sid);
    idx = idx === -1 ? cfg[model].push(sid) - 1 : idx;

    setCfg('MODEL_SID_TREE', cfg);

    return `${validModelId}-${idx}`;
}

function prepareTopic(topic) {
    const rootTopic = getCfg('rootTopic');
    const deviceId = getCfg('deviceId');

    return `${rootTopic}/${deviceId}/${topic}`
}

const MQTT_LOGIN  = '';
const MQTT_PASS   = '';
const DEVICE_ID   = '';
const PRODUCT_ID  = '';
const PRODUCT_VER = '1';

setCfg('MQTT_URI', 'mqtts://cloud.2smart.com:8883');
setCfg('MQTT_USER', MQTT_LOGIN);
setCfg('MQTT_PASS', MQTT_PASS);

setCfg('rootTopic', `${MQTT_LOGIN}/sweet-home`);
setCfg('errorTopic', 'errors');
setCfg('deviceId', DEVICE_ID);
setCfg('color', {
    red: '255', green: '255', blue: '255', brightness: '30'
});
setCfg('gwStatus', 'disconnected');


const device = {
    name            : 'Xiaomi Gateway',
    mac             : 'unknown',
    localIp         : '0.0.0.0',
    implementation  : '-',
    state           : 'init',
    firmwareName    : PRODUCT_ID,
    firmwareVersion : PRODUCT_VER,
    nodes           : {},
    options         : {}
};

if (!getCfg('TOPIC_SID_MAP')) setCfg('TOPIC_SID_MAP', {});
if (!getCfg('device')) setCfg('device', device);

// Global Functions
flow.set('setCfg', setCfg);
flow.set('getCfg', getCfg);
flow.set('getSensorId', getSensorId);
flow.set('getModelId', getModelId);
flow.set('beautifyModelId', beautifyModelId);
flow.set('prepareTopic', prepareTopic);

const setCfg = flow.get('setCfg');
const getCfg = flow.get('getCfg');
const getModelId = flow.get('getModelId');
const getSensorId = flow.get('getSensorId');

const device = getCfg('device');
const deviceId = getCfg('deviceId');

const { data, sid, model, cmd } = msg.payload;

const sensors = {
    status: 'status',
    temperature: 'temperature',
    humidity: 'humidity',
    pressure: 'pressure',
    channel_0: 'channel_0',
    channel_1: 'channel_1',
    plug_status: 'plug_status'
};
const telemetries = {
    voltage: 'voltage',
    rotate: 'rotate',
    power_consumed: 'power_consumed',
    load_power: 'load_power',
    inuse: 'inuse'
};
const options = {
    rgb: 'rgb'
};

if (model === 'plug') {
    if (data.status) {
        data.plug_status = data.status;
        delete data.status;
    }
    if (data.voltage) delete data.voltage;
}
if (data['dual_channel']) {
    let v;

    switch (data['dual_channel']) {
        case 'long_both_click':
            v = 'long_click';
            break;
        case 'both_click':
            v = 'click';
            break;
        case 'double_both_click':
            v = 'double_click';
            break;
        default:
            break;
    }   

    if (v) {
        data['channel_0'] = v;
        data['channel_1'] = v;
        delete data['dual_channel'];
    }
}

const sensorPayload = [];
const telemetryPayload = [];
const optionPayload = [];
const nodePayload = [];
const validId = Object.assign(sensors, telemetries, options);

Object.keys(data).forEach(id => {
    if (!validId[id]) return;

    switch (id) {
        default: {
            const modelId = getModelId(model);
            const sensorId = getSensorId(id, sid);
            const isNewNode = !device.nodes?.[modelId] || false;
            const isNewSensor = !device.nodes?.[modelId]?.[sensorId] || false;
            const payload = {
                sid,
                model,
                nodeId     : modelId,
                value      : data[id],
                propertyId : sensorId,
                type       : id
            };

            if (isNewNode) {
                device.nodes[modelId] = {};
                nodePayload.push({ isNew: isNewNode, modelId });
            }
            if (isNewSensor) device.nodes[modelId][sensorId] = sensorId;

            if (sensors[id]) {
                sensorPayload.push({payload, isNew: isNewSensor, modelId});
            }
            if (telemetries[id]) {
                telemetryPayload.push({payload, isNew: isNewSensor});
            }
            break;
        }
    }
});

setCfg('device', device);

return [ nodePayload, sensorPayload, telemetryPayload, optionPayload ];

const sensorsInfo = {
    status : {
        name     : 'Status',
        settable : 'false',
        retained : 'true',
        dataType : 'boolean',
        unit     : '#',
        format   : '',
        value    : ''
    },
    humidity : {
        name     : 'Humidity',
        settable : 'false',
        retained : 'true',
        dataType : 'float',
        unit     : '%',
        format   : '',
        value    : ''
    },
    temperature : {
        name     : 'Temperature',
        settable : 'false',
        retained : 'true',
        dataType : 'float',
        unit     : '°C',
        format   : '',
        value    : ''
    },
    pressure : {
        name     : 'Pressure',
        settable : 'false',
        retained : 'true',
        dataType : 'float',
        unit     : 'kpa',
        format   : '',
        value    : ''
    },
    plug_status: {
        name     : 'Status',
        settable : 'true',
        retained : 'true',
        dataType : 'boolean',
        unit     : '#',
        format   : '',
        value    : ''
    },
    channel_0: {
        name     : 'Left switch',
        settable : 'false',
        retained : 'true',
        dataType : 'string',
        unit     : '#',
        format   : '',
        value    : ''
    },
    channel_1: {
        name     : 'Right switch',
        settable : 'false',
        retained : 'true',
        dataType : 'string',
        unit     : '#',
        format   : '',
        value    : ''
    }
};
let { value, propertyId, sid, type, nodeId, model } = msg.payload;

const sensor = sensorsInfo[type];

if (!sensor) return;

sensor.model = model;
sensor.sid = sid;
sensor.id = propertyId;
sensor.nodeId = nodeId;
sensor.type = 'sensor';

if (value === 'unknown') value = '';

if (['off','on'].includes(value)) {
    value = value === 'on' ? 'true' : 'false';
}

if (['no_motion', 'motion'].includes(value)) {
    value = value === 'motion' ? 'true' : 'false';
}

switch (type) {
    case 'humidity':
    case 'temperature':
        // HARDCODE xiaomi sending 10000 temp on init
        sensor.value = value === '10000' ? '' : `${(value / 100).toFixed(1)}`;
        break;
    case 'pressure':
        sensor.value = `${(value / 1000).toFixed(1)}`;
        break;
    case 'channel_0':
        if (msg.model === 'remote.b186acn01' || msg.model === '86sw1') sensor.name = 'Switch';
        sensor.value = value;
        break;
    default:
        sensor.value = value;
        break;
}

msg.payload = sensor;

// start "no motion" timeout
if (model === 'motion' && value === 'true') {
    const timerId = `timeout-${sid}`;
    // reset prev timeout
    clearTimeout(flow.get(timerId));

    const timer = setTimeout(() => {
        sensor.value = 'false';
        msg.payload = sensor;
        node.send(msg);
    }, 30000); // 30s
    flow.set(timerId, timer);
}

return msg;

const telemetryInfo = {
    voltage : {
        id       : 'battery',
        name     : 'Battery',
        settable : 'false',
        retained : 'true',
        dataType : 'integer',
        unit     : '%',
        format   : '',
