        id       : 'time',
        name     : 'Rotation time',
        settable : 'false',
        retained : 'true',
        dataType : 'integer',
        unit     : 'ms',
        format   : '',
        value    : ''
    },
    power_consumed : {
        id       : 'power-consumed',
        name     : 'Power consumed',
        settable : 'false',
        retained : 'true',
        dataType : 'float',
        unit     : 'W',
        format   : '',
        value    : ''
    },
    load_power : {
