        value    : ''
    }
};

function batteryLvl(voltage) {
    const VOLTAGE_MAX = global.get('VOLTAGE_MAX');
    const VOLTAGE_MIN = global.get('VOLTAGE_MIN');
    const val = +voltage;
    const lvl = ((val - VOLTAGE_MIN) / (VOLTAGE_MAX - VOLTAGE_MIN) * 100).toFixed(0);

    return Number(lvl) > 100 ? 100 : lvl;
}

const { value, propertyId, sid, nodeId, type, model } = msg.payload;
const telemetry = telemetryInfo[type];

if (!telemetry) return null;

switch (propertyId) {
    case 'inuse':
        telemetry.value = value === '1' ? 'true' : 'false';
        break;
    case 'voltage':
        telemetry.value = batteryLvl(value);
        break;
    default:
        telemetry.value = value;
        break;
}

if (nodeId) telemetry.nodeId = nodeId;

telemetry.model = model;
telemetry.sid = sid;
telemetry.type = 'telemetry';

msg.payload = telemetry;

return msg;

const getCfg = flow.get('getCfg');
const setCfg = flow.get('setCfg');

const rootTopic = getCfg('rootTopic');
const deviceId = getCfg('deviceId');
const topicSID = getCfg('TOPIC_SID_MAP');
const { id, nodeId, type, sid, model } = msg.payload;
const baseTopic = nodeId ? `${rootTopic}/${deviceId}/${nodeId}` : `${rootTopic}/${deviceId}`;

switch (type) {
    case 'sensor':
        msg.payload.topic = `${baseTopic}/${id}`;
        break;
    case 'options':
    case 'telemetry':
        msg.payload.topic = `${baseTopic}/$${type}/${id}`;
        break;
    default:
        break;
}

topicSID[msg.payload.topic] = {
    sid,
    model
};

setCfg('TOPIC_SID_MAP', topicSID);

// return msg.isNew ? [msg, msg] : [null, msg];
return [msg, msg];

const { topic, settable } = msg.payload;

msg.payload = settable;
msg.topic = `${topic}/$settable`;

return msg;

const { topic, retained } = msg.payload;

msg.payload = retained;
msg.topic = `${topic}/$retained`;

return msg;

const { topic, dataType } = msg.payload;

msg.payload = dataType;
msg.topic = `${topic}/$datatype`;

return msg;

const { topic, unit } = msg.payload;

msg.payload = unit;
msg.topic = `${topic}/$unit`;

return msg;

const { topic, format } = msg.payload;

msg.payload = format;
msg.topic = `${topic}/$format`;

return msg;

const { topic, value } = msg.payload;

msg.payload = `${value}`;
msg.topic = topic;

return msg;

const { name, topic } = msg.payload;

msg.payload = name;
msg.topic = `${topic}/$name`;

return msg;

const optionInfo = {
    rgb : {
        id       : 'rgb',
        name     : 'Color',
        settable : 'true',
        retained : 'true',
        dataType : 'color',
        unit     : '#',
        format   : 'rgb',
        value    : ''
    },
    brightness : {
        id       : 'brightness',
        name     : 'Brightness',
        settable : 'true',
        retained : 'true',
        dataType : 'integer',
        unit     : '%',
        format   : '0:100',
        value    : ''
    },
    switcher : {
        id       : 'switch',
        name     : 'Switch',
        settable : 'true',
        retained : 'true',
        dataType : 'boolean',
        unit     : '#',
        format   : '',
        value    : ''
    }
};

const { value, propertyId, nodeId, type, sid, model } = msg.payload;
const option = optionInfo[type];

if (!option) return null;

if (nodeId) option.nodeId = nodeId;

option.value = value;
option.type = 'options';
option.sid = sid;
option.model = model;

msg.payload = option;

return msg;

if (!msg.isNew) return; // skip duplicated publish

const beautifyModelId = flow.get('beautifyModelId');
const payload = {
    id : msg.modelId,
    state: 'ready',
    name: beautifyModelId(msg.modelId)
}

return { payload };

const getCfg = flow.get('getCfg');

const rootTopic = getCfg('rootTopic');
const deviceId = getCfg('deviceId');
const { id, name } = msg.payload;

msg.payload = name;
msg.topic = `${rootTopic}/${deviceId}/${id}/$name`;

