const setCfg = flow.get('setCfg');
const mqttStatusList = {
    CONNECTED    : 'node-red:common.status.connected',
    CONNECTING   : 'node-red:common.status.connecting',
    DISCONNECTED : 'node-red:common.status.disconnected'
};

const { status: { text } } = msg;
let connected = false;

switch (text) {
    case mqttStatusList.CONNECTED:
        setCfg('CLOUD_MQTT_CONNECTED', true);
        connected = true;
        break;
    default:
        setCfg('CLOUD_MQTT_CONNECTED', false);
        break;
}

return { payload: { connected } };

const prepareTopic = flow.get('prepareTopic');

return [
    {
        action: 'subscribe',
        topic: [
            prepareTopic(`+/+/set`),
            prepareTopic(`+/$options/+/set`),
            prepareTopic(`+/$telemetry/+/set`),
        ]
    }
];

const getCfg = flow.get('getCfg');

if (!getCfg('MQTT_USER') || !getCfg('MQTT_PASS')) return;

return [
    {
        "action": "connect",
        "broker": {
            "url": getCfg('MQTT_URI'),
            "keepalive": 30,
            "clean": true,
            "username": getCfg('MQTT_USER'),
            "password": getCfg('MQTT_PASS'),
            "will": {
                "topic": `${getCfg('rootTopic')}/${getCfg('deviceId')}/$state`,
                "qos": 0,
                "retain": true,
                "payload": "lost"
            }
        }
    }
];

const getCfg = flow.get('getCfg');
const device = getCfg('device');

if (device.state !== 'ready') return;

const prepareTopic = flow.get('prepareTopic');

return { payload: 'p', topic: prepareTopic('$heartbeat') };

const getCfg = flow.get('getCfg');
const setCfg = flow.get('setCfg');
const device = getCfg('device');
const { status: { fill } } = msg;

if (fill === 'green' && getCfg('gwStatus') !== 'connected') {
    setCfg('gwStatus', 'connected');
    device.state = 'ready';
    return ({ payload: { state: 'ready' }});
}

return null;
